import React, { useEffect, useState } from "react";
import { useParams } from "react-router";

function Decks() {
  const [deck, setDeck] = useState({});
  const { deckId } = useParams();

  useEffect(() => {
    const abortController = new AbortController();

    async function loadDeck() {
      try {
        const response = await fetch(`http://localhost:5000/decks/${deckId}`, {
          signal: abortController.signal,
        });
        const deck = await response.json();
        setDeck(deck);
      } catch (error) {
        if (error.name !== "AbortError") {
          console.log(error);
        }
      }
    }

    loadDeck();

    return () => {
      abortController.abort(); // cancels any pending request or response
    };
  }, [deckId]);

  if (deck.id) {
    // return (
    //   <div className="container" width="36rem" margin="0 auto">
    //     {/* TODO: Implement the screen starting here */}
    //     <a class="btn btn-secondary" href="#" role="button">
    //       Create deck
    //     </a>
    //     <br></br>
    //     <br></br>
    //     <div class="card">
    //       <h5 class="card-header">
    //         Rendering in React
    //         <p class="float-right font-weight-light">3 cards</p>
    //       </h5>
    //       <div class="card-body">
    //         <p class="card-text">
    //           I feel like this is actually supposed to be a card fetched from
    //           the API.
    //         </p>
    //       </div>
    //       <div class="card-footer">
    //         <a href="#" class="btn btn-secondary mr-2">
    //           View
    //         </a>
    //         <a href="#" class="btn btn-primary">
    //           Study
    //         </a>
    //         <a href="#" class="btn btn-danger float-right">
    //           Delete
    //         </a>
    //       </div>
    //     </div>
    //   </div>
    // );
    return Object.entries(deck).map(([key, value]) => (
      <div key={key}>
        <label>{key}</label>: {JSON.stringify(value)}
        <hr />
      </div>
    ));
  }
  return "Loading...";
}

export default Decks;
