import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link, Switch, Route } from "react-router-dom";
import { listDecks } from "../../utils/api";
import Study from "./Study";

function Decks() {
  const [decks, setDecks] = useState([]);
  const { deckId } = useParams();

  useEffect(() => {
    async function getDecks() {
      const decksList = await listDecks(deckId);
      setDecks(decksList);
    }
    getDecks();
  }, [deckId]);

  const mappedDecks = decks.map((deck) => {
    return (
      <div class="card mb-5" key={deck.id} style={{ width: "36rem" }}>
        <h5 class="card-header">
          {deck.name}
          <p class="float-right font-weight-light">{deck.cards.length}</p>
        </h5>
        <div class="card-body">
          <p class="card-text">{deck.description}</p>
        </div>
        <div class="card-footer">
          <Link
            to={`decks/${deck.id}`}
            class="btn btn-secondary mr-2"
            title="Edit deck"
          >
            <span class="oi oi-eye mr-2" /> View
          </Link>
          <Link to={`/decks/${decks.id}/study`}>
            <button class="btn btn-primary">Study</button>
          </Link>
          <Route path="/decks/:deckId/study">
            <Study />
          </Route>
          <button class="btn btn-danger float-right">Delete</button>
        </div>
      </div>
    );
  });

  return <div>{mappedDecks}</div>;
}

export default Decks;
