import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link, Switch, Route } from "react-router-dom";
import { listDecks, deleteDeck } from "../../utils/api";
import Study from "./Study";

function Decks() {
  const [decks, setDecks] = useState([]);
  const { deckId } = useParams();

  useEffect(() => {
    async function getDecks() {
      const decksList = await listDecks(deckId);
      setDecks(decksList);
    }
    getDecks();
  }, [deckId]);

  function deleteDeckHandler(deckId) {
    const deleted = window.confirm(
      "Delete this deck?\n\nYou will not be able to recover it."
    );
    if (deleted) {
      //console.log("deleteDeckHandler", deleted, deckId);
      deleteDeck(deckId).then(loadDeck);
    }
  }

  const mappedDecks = decks.map((deck) => {
    return (
      <div class="card mb-5" key={deck.id} style={{ width: "36rem" }}>
        <h5 class="card-header">
          {deck.name}
          <p class="float-right font-weight-light">{deck.cards.length} cards</p>
        </h5>
        <div class="card-body">
          <p class="card-text">{deck.description}</p>
        </div>
        <div class="card-footer">
          <Link
            to={`decks/${deck.id}`}
            class="btn btn-secondary mr-2"
            title="View deck"
          >
            <span class="oi oi-eye mr-2" /> View
          </Link>
          <Link
            to={`/decks/${deck.id}/study`}
            class="btn btn-primary"
            title="Study deck"
          >
            <span class="oi oi-book" /> Study
          </Link>
          <button
            class="btn btn-danger float-right"
            title="Delete Deck"
            onClick={() => deleteHandler(deck.id)}
          >
            <span className="oi oi-trash" />
            Delete
          </button>
        </div>
      </div>
    );
  });

  return (
    <div>
      <Link to="decks/new" class="btn btn-secondary mb-2">
        <span class="oi oi-plus" />
        {` Create deck`}
      </Link>
      {mappedDecks}
    </div>
  );
}

export default Decks;
