import React, { useEffect, useState } from "react";
import { useParams, useHistory, Link } from "react-router-dom";
import { readDeck } from "../../utils/api";
import Card from "./Card";

function Study() {
  const [deck, setDeck] = useState({ name: "Loading...", cards: [] });
  const [cardNumber, setCardNumber] = useState(1);

  const { deckId } = useParams();
  const history = useHistory();

  useEffect(() => {
    async function getDeck() {
      const cardsList = await readDeck(deckId);
      setDeck(cardsList);
    }
    getDeck();
  }, [deckId]);

  const cardCount = deck.cards.length;
  const cardTitle = `Card ${cardNumber} of ${cardCount}`;
  const card = deck.cards[cardNumber - 1];

  function nextHandler() {
    if (cardNumber === cardCount) {
      const returnToHomePage = !window.confirm(
        "Restart cards?\n\nClick 'cancel' to return to the home page."
      );
      return returnToHomePage ? history.push("/") : setCardNumber(1);
    }
    setCardNumber((prevState) => Math.min(cardCount, prevState + 1));
  }

  if (cardCount <= 2) {
    return (
      <div>
        <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
          <ol class="breadcrumb">
            <li class="breadcrumb-item">
              <Link to="/">
                <span class="oi oi-home mr-1" />
                Home
              </Link>
            </li>
            <li class="breadcrumb-item">
              <Link to="/">{deck.name}</Link>
            </li>
            <li class="breadcrumb-item active" aria-current="page">
              Study
            </li>
          </ol>
        </nav>
        <h1>Study: {deck.name}</h1>
        <h2>Not enough cards.</h2>
        <p>
          You need at least 3 cards to study. There are {cardCount} cards in the
          deck.
        </p>
        <Link to={`/decks/${deck.id}/cards/new`} class="btn btn-primary">
          <span class="oi oi-plus" /> Add cards
        </Link>
      </div>
    );
  }

  return (
    <div>
      <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <Link to="/">
              <span class="oi oi-home mr-1" />
              Home
            </Link>
          </li>
          <li class="breadcrumb-item">
            <Link to="/">{deck.name}</Link>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            Study
          </li>
        </ol>
      </nav>
      <h1>Study: {deck.name}</h1>
      <Card card={card} title={cardTitle}>
        <button type="button" class="btn btn-primary" onClick={nextHandler}>
          Next
        </button>
      </Card>
    </div>
  );
}

export default Study;
