import React, { useState, useEffect } from "react";
import { Link, useParams, useHistory } from "react-router-dom";
import { readDeck, updateDeck } from "../../utils/api";
import DeckForm from "./DeckForm";

function DeckEdit() {
  const { deckId } = useParams();
  const history = useHistory();

  const [deck, setDeck] = useState({ name: "", description: "" });

  useEffect(() => {
    async function getDeck() {
      const cardsList = await readDeck(deckId);
      setDeck(cardsList);
    }
    getDeck();
  }, [deckId]);

  function buttonSubmitHandler(updatedDeck) {
    updateDeck(updatedDeck).then((editedDeck) =>
      history.push(`/decks/${editedDeck.id}`)
    );
  }

  function cancel() {
    history.goBack();
  }

  const child = deck.id ? (
    <DeckForm
      onCancel={cancel}
      onSubmit={buttonSubmitHandler}
      initialState={deck}
    />
  ) : (
    <p>Loading...</p>
  );

  return (
    <div>
      <div>
        <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
          <ol class="breadcrumb">
            <li class="breadcrumb-item">
              <Link to="/">
                <span class="oi oi-home mr-1" />
                Home
              </Link>
            </li>
            <li class="breadcrumb-item">
              <Link to="/">{deck.name}</Link>
            </li>
            <li class="breadcrumb-item active" aria-current="page">
              Edit Deck
            </li>
          </ol>
        </nav>
      </div>
      <h1>Edit Deck</h1>
    </div>
  );
}

export default DeckEdit;
