import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { createDeck } from "../utils/api";

function DeckCreate() {
  const history = useHistory();
  const [deck, setDeck] = useState({ name: "", description: "" });

  function buttonSubmitHandler(deck) {
    createDeck(deck).then((createdDeck) =>
      history.push(`/decks/${createdDeck.id}`)
    );
  }

  function formSubmitHandler(event) {
    event.preventDefault();
    buttonSubmitHandler(deck);
  }

  function changeHandler({ target: { name, value } }) {
    setDeck((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  }

  function cancel() {
    history.goBack();
  }

  return (
    <div>
      <div>
        <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
          <ol class="breadcrumb">
            <li class="breadcrumb-item">
              <Link to="/">
                <span class="oi oi-home mr-1" />
                Home
              </Link>
            </li>
            <li class="breadcrumb-item active" aria-current="page">
              Create Deck
            </li>
          </ol>
        </nav>
      </div>
      <h1>Create Deck</h1>
      <form onSubmit={formSubmitHandler} style={{ width: "36rem" }}>
        <div class="form-group">
          <label htmlFor="name">Name</label>
          <input
            name="name"
            type="text"
            class="form-control"
            id="name"
            value={deck.name}
            required={true}
            placeholder="Deck Name"
            onChange={changeHandler}
          />
        </div>
        <div class="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            name="description"
            class="form-control"
            id="description"
            rows="4"
            value={deck.description}
            required={true}
            placeholder="Brief description of the deck"
            onChange={changeHandler}
          ></textarea>
        </div>
        <button type="cancel" class="btn btn-secondary mr-2" onClick={cancel}>
          Cancel
        </button>
        <button
          type="submit"
          class="btn btn-primary"
          onSubmit={buttonSubmitHandler}
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default DeckCreate;
