import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link, Switch, Route } from "react-router-dom";
import { listDecks } from "../../utils/api";
import Study from "./Study";

function Decks() {
  const [decks, setDecks] = useState([]);
  const { deckId } = useParams();

  useEffect(() => {
    async function getDecks() {
      const decksList = await listDecks();
      setDecks(decksList);
    }
    getDecks();
  }, []);
  console.log(decks);

  // console.log("decks:" + decks);
  // console.log("deckId:" + deckId);
  //const list = decks.map((deck) => deck.id);
  //const deck = decks.find((deck) => `${deck.id}` === deckId);

  return (
    <div class="card" width="36rem" margin="0 auto">
      <h5 class="card-header">
        {decks.name}
        <p class="float-right font-weight-light">3 cards</p>
      </h5>
      <div class="card-body">
        <p class="card-text">{decks.description}</p>
      </div>
      <div class="card-footer">
        <button class="btn btn-secondary mr-2">View</button>
        <Link to="Study">
          <button class="btn btn-primary">Study</button>
        </Link>
        <Route path="Study">
          <Study />
        </Route>
        <button class="btn btn-danger float-right">Delete</button>
      </div>
    </div>
  );
}

export default Decks;

