import React, { useEffect, useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { createCard, readDeck } from "../../utils/api";

function CardCreate() {
  const history = useHistory();
  const { deckId } = useParams();
  const [deck, setDeck] = useState({ cards: [] });

  useEffect(() => {
    readDeck(deckId).then(setDeck);
  }, [deckId]);

  function buttonSubmitHandler(card) {
    createCard(card).then((createdCard) =>
      history.push(`/decks/${createdCard.id}`)
    );
  }

  function formSubmitHandler(event) {
    event.preventDefault();
    buttonSubmitHandler(deck);
  }

  function changeHandler() {
    history.push(`/decks/${deckId}`);
  }

  function cancel() {
    history.goBack();
  }

  return (
    <div>
      <div>
        <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
          <ol class="breadcrumb">
            <li class="breadcrumb-item">
              <Link to="/">
                <span class="oi oi-home mr-1" />
                Home
              </Link>
            </li>
            <li class="breadcrumb-item">
              <Link to="/">{deck.name}</Link>
            </li>
            <li class="breadcrumb-item active" aria-current="page">
              Add Card
            </li>
          </ol>
        </nav>
      </div>
      <h1>Add Card</h1>
      <form onSubmit={formSubmitHandler} style={{ width: "36rem" }}>
        <div class="form-group">
          <label htmlFor="name">Name</label>
          <textarea
            name="front"
            class="form-control"
            id="front"
            rows="3"
            required={true}
            placeholder="Front side of card"
            onChange={changeHandler}
          ></textarea>
        </div>
        <div class="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            name="back"
            class="form-control"
            id="back"
            rows="3"
            required={true}
            placeholder="Back side of card"
            onChange={changeHandler}
          ></textarea>
        </div>
        <button type="cancel" class="btn btn-secondary mr-2" onClick={cancel}>
          Cancel
        </button>
        <button
          type="submit"
          class="btn btn-primary"
          onSubmit={buttonSubmitHandler}
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default CardCreate;
