import React, { useEffect, useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { deleteCard, deleteDeck, readDeck } from "../../utils/api";
import Cards from "./Cards";

function DeckView() {
  const [deck, setDeck] = useState({ cards: [] });
  const { deckId } = useParams();
  const history = useHistory();

  useEffect(loadDeck, [deckId]);

  function loadDeck() {
    readDeck(deckId).then(setDeck);
  }

  function deleteDeckHandler() {
    const deleted = window.confirm(
      "Delete this deck?\n\nYou will not be able to recover it."
    );
    if (deleted) {
      deleteDeck(deck.id).then(() => history.push("/decks"));
    }
  }

  function deleteCardHandler(cardId) {
    const deleted = window.confirm(
      "Delete this card?\n\nYou will not be able to recover it."
    );
    if (deleted) {
      console.log("deleteCardHandler", deleted, cardId);
      deleteCard(cardId).then(loadDeck);
    }
  }

  return (
    <div>
      <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <Link to="/">
              <span class="oi oi-home" />
              Home
            </Link>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            {deck.name}
          </li>
        </ol>
      </nav>
      <div class="card mb-5" key={deck.id} style={{ width: "36rem" }}>
        <h5 class="card-header">{deck.name}</h5>
        <div class="card-body">
          <p class="card-text">{deck.description}</p>
        </div>
        <div class="card-footer">
          <Link
            to={`TODO: create link`}
            class="btn btn-secondary mr-2"
            title="Edit deck"
          >
            <span class="mr-2" /> Edit
          </Link>
          <Link
            to={`/decks/${deck.id}/study`}
            class="btn btn-primary mr-2"
            title="Study deck"
          >
            <span class="oi oi-book" /> Study
          </Link>
          <Link
            to={`TODO: create link`}
            class="btn btn-primary mr-2"
            title="Study deck"
          >
            <span class="" /> Add cards
          </Link>
          <button class="btn btn-danger float-right" title="Delete Deck">
            <span className="oi oi-trash" onClick={deleteDeckHandler} />
            Delete
          </button>
        </div>
        <Cards deck={deck} onClick={deleteCardHandler} />
      </div>
    </div>
  );
}

export default DeckView;

