import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link, Switch, Route } from "react-router-dom";

function Decks() {
  const [deck, setDeck] = useState({});
  //const { deckId } = useParams();

  useEffect(() => {
    const abortController = new AbortController();

    async function loadDeck() {
      try {
        const response = await fetch(`http://localhost:5000/decks/1`, {
          signal: abortController.signal,
        });
        const deck = await response.json();
        setDeck(deck);
      } catch (error) {
        if (error.name !== "AbortError") {
          console.log(error);
        }
      }
    }

    loadDeck();

    return () => {
      abortController.abort(); // cancels any pending request or response
    };
  }, []);

  //if (deck.id) {
  return (
    <div class="card" width="36rem" margin="0 auto">
      <h5 class="card-header">
        {deck.name}
        <p class="float-right font-weight-light">3 cards</p>
      </h5>
      <div class="card-body">
        <p class="card-text">{deck.description}</p>
      </div>
      <div class="card-footer">
        <button class="btn btn-secondary mr-2">View</button>
        <Link to="/components/Study">
          <button class="btn btn-primary">Study</button>
        </Link>
        <Route path="components/Study">
          <Study />
        </Route>
        <button class="btn btn-danger float-right">Delete</button>
      </div>
    </div>
  );

  // return Object.entries(deck).map(([key, value]) => (
  //   <div key={key}>
  //     <label>{key}</label>: {JSON.stringify(value)}
  //     <hr />
  //   </div>
  // ));
  //}

  //return "Loading...";
}

export default Decks;
