import React, { useEffect, useState } from "react";
import { useHistory, useParams, Link } from "react-router-dom";
import { readCard, readDeck, updateCard } from "../../utils/api";
import CardForm from "./CardForm";

function CardEdit({ title }) {
  const history = useHistory();
  const { deckId, cardId } = useParams();

  const [card, setCard] = useState({ front: "", back: "" });
  const [deck, setDeck] = useState({ cards: [] });

  useEffect(() => {
    readDeck(deckId).then(setDeck);
    readCard(cardId).then(setCard);
  }, [deckId, cardId]);

  function doneHandler() {
    history.push(`/decks/${deck.id}`);
  }

  function buttonSubmitHandler(card) {
    updateCard(card).then(doneHandler);
  }

  const child = card.id ? (
    <CardForm
      onSubmit={buttonSubmitHandler}
      onDone={doneHandler}
      deckName={deck.name}
      initialState={card}
      doneButtonLabel="Cancel"
    />
  ) : (
    <p>Loading...</p>
  );

  return (
    <div>
      <div>
        <nav aria-label="breadcrumb" style={{ width: "36rem" }}>
          <ol class="breadcrumb">
            <li class="breadcrumb-item">
              <Link to="/">
                <span class="oi oi-home mr-1" />
                Home
              </Link>
            </li>
            <li class="breadcrumb-item">
              <Link to="/">{deck.name}</Link>
            </li>
            <li class="breadcrumb-item active" aria-current="page">
              Edit Card {card}
            </li>
          </ol>
        </nav>
      </div>
      <h1>Edit Card</h1>
      {child}
    </div>
  );
}

export default CardEdit;
