import React from "react";
import { Link } from "react-router-dom";

function Cards({ deck }) {
  const { cards = [] } = deck;

  const mappedCards = cards.map((card) => (
    <div>
      <div class="card-group">
        <div class="card mb-3">
          <div class="card-body">
            <p class="card-text">{card.front}</p>
          </div>
        </div>
      </div>
      <div class="col-sm-6">
        <div class="card mb-3">
          <div class="card-body">
            <p class="card-text">{card.back}</p>
            <button
              class="btn btn-danger float-right"
              title="Delete Deck"
              // TODO: onClick={() => deleteHandler(deck.id)}
            >
              <span className="oi oi-trash" />
              Delete
            </button>
            <Link
              to={`TODO: create link`}
              class="btn btn-secondary mr-2 float-right"
              title="Edit deck"
            >
              <span class="mr-2" /> Edit
            </Link>
          </div>
        </div>
      </div>
    </div>
  ));

  return (
    <div>
      <h3 class="mt-3 ml-3">Cards</h3>
      {mappedCards}
    </div>
  );
}

export default Cards;
